package demo;public class Test8 {	public static void main(String[] args) {		String moneybase = "550.00";		String money = "0";		if(moneybase.contains(".")){    		money = moneybase.substring(0, moneybase.indexOf("."));    	}else{    		money = moneybase;    	}		String prizeplan = "100~499;15;500~999;25;1000~以上;35";		String[] plans = prizeplan.split(";");    	int length = plans.length;    	String prize = null;    	if(length==6){    		String p1 = (plans[4].split("~"))[0];    		System.out.println(p1);    		String p2 = (plans[2].split("~"))[0];    		String p3 = (plans[0].split("~"))[0];    		if(Integer.valueOf(p1) <= Integer.valueOf(money)){    			prize = plans[5];    		}else if(Integer.valueOf(p2) <= Integer.valueOf(money)){    			prize = plans[3];    		}else if(Integer.valueOf(p3) <= Integer.valueOf(money)){    			prize = plans[1];    		}else{    			prize = "0";    		}    	}    	else{    		String p1 = (plans[2].split("~"))[0];    		String p2 = (plans[0].split("~"))[0];    		if(Integer.valueOf(p1) <= Integer.valueOf(money)){    			prize = plans[3];    		}else if(Integer.valueOf(p2) <= Integer.valueOf(money)){    			prize = plans[1];    		}else{    			prize = "0";    		}    	}    	System.out.println(prize);	}}